mapper.update:
update t_b_article
title=#{title}, summary=#{summary}, content=#{content}, category=#{category}, type=#{type}, tag=#{tag}, read_amount=#{readAmount}, comment_amount=#{commentAmount}, approval_amount=#{approvalAmount}, oppose_amount=#{opposeAmount}, update_time=#{updateTime}, audit_status=#{auditStatus}
where id=#{id}

mapper.select:
select art.*
from t_b_article art
where art.userId=#{0}

select approval_amount
from t_b_article
where id=#{0}

select oppose_amount
from t_b_article
where id=#{0}
